/* ------------------------------------------------------------------
      Send Smileys to UI
------------------------------------------------------------------ */
figma.ui.onmessage = (msg) => {
  if (msg.type === "request-smileys") {
    figma.ui.postMessage({
      type: "load-smileys",
      smileys: smileys,
    });
  }

  if (msg.type === "insert-smiley") {
    const smiley = smileys.find((s) => s.name === msg.smileyType);
    if (!smiley) return;

    const svgString = smiley.svg
      .replace(
        /class="base" fill=".*?"/,
        `class="base" fill="${msg.color.light}"`
      )
      .replace(
        /class="face" fill=".*?"/g,
        `class="face" fill="${msg.color.dark}"`
      );

    /* ------------------------------------------------------------------
    Convert SVG to a Figma node
    ------------------------------------------------------------------ */
    const node = figma.createNodeFromSvg(svgString);
    figma.currentPage.appendChild(node);
    figma.viewport.scrollAndZoomIntoView([node]);

    figma.closePlugin();
  }
};
